import React, { Component, Fragment } from 'react'
import firebase from '../../firebase';

import './Explore.css';

// Components
import CategoryCard from '../components/CategoryCard'

class Explore extends Component {
  constructor(props) {
    super(props);

    this.state = {
      skillsArr: []
    }
  }

  componentDidMount() {
    const skillsRef = firebase.database().ref('skills');
    let skillsArr = []



    skillsRef.once('value').then(snapshot => {
      snapshot.forEach(keysSnapshot => {
        var obj = keysSnapshot.val();
        skillsArr.push(obj);
      })
    }).then(() => {
      console.log(skillsArr);
      this.setState({
        skillsArr: skillsArr
      })
    })
  }

  render() {
    const categories = this.state.skillsArr;

    return (
      <div className='explore-container'>
        <div className='explore-title-container'>
          <div className="explore-title">Explore</div>
          <div className="explore-subtitle">Explore different skillsharing categories.</div>
        </div>
        <div className='categories-container'>
          {categories.map(category => {
            console.log(category)

            return (
              <CategoryCard name={category.name} desc={category.desc} img={category.img} subskills={category.subskills}/>
            )
          })}
        </div>
      </div>
    )
  }
}

export default Explore
